DROP TRIGGER IF EXISTS `join`;
DELIMITER ;;
CREATE TRIGGER `join` AFTER INSERT ON `activity_person` FOR EACH ROW update activity set num = num+1 where new.activity_id = activity.id
;
;;
DELIMITER ;
DROP TRIGGER IF EXISTS `exit`;
DELIMITER ;;
CREATE TRIGGER `exit` AFTER DELETE ON `activity_person` FOR EACH ROW update activity set num = num-1 where old.activity_id = activity.id
;;
DELIMITER ;
DROP TRIGGER IF EXISTS `praise`;
DELIMITER ;;
CREATE TRIGGER `praise` AFTER INSERT ON `praise` FOR EACH ROW update photo set praise = praise+1 where new.photo_id = photo.id
;;
DELIMITER ;
DROP TRIGGER IF EXISTS `unpraise`;
DELIMITER ;;
CREATE TRIGGER `unpraise` AFTER DELETE ON `praise` FOR EACH ROW update photo set praise = praise-1 where old.photo_id = photo.id
;;
DELIMITER ;
DROP TRIGGER IF EXISTS `delImg`;
DELIMITER ;;
CREATE TRIGGER `delImg` AFTER DELETE ON `scenic` FOR EACH ROW delete from images where old.id = images.scenic_id
;;
DELIMITER ;
